/* * * * * * *
 *
 *   For the speed option.
 */
static void
doOptSpeed(
    tOptions*   pOptions,
    tOptDesc*   pOptDesc )
{
    /* extracted from clktest-opts.def, line 125 */
    /*
     * speed table
     */
    static struct speeds {
            const unsigned int bps;
            const unsigned int rate;
    } *spd, speedtab[] = {
        { 300,          B300 },
        { 1200,         B1200 },
        { 2400,         B2400 },
        { 4800,         B4800 },
        { 9600,         B9600 },
        { 19200,        EXTA },
        { 38400,        EXTB },
        { 0,            0 }
    };
    unsigned long tmp;

    if (!atouint(pOptDesc->pzLastArg, &tmp)) {
        fprintf(stderr, "%s error:  not a number:  ``%s''\n",
                pOptions->pzProgName, pOptDesc->pzLastArg );
        USAGE( EXIT_FAILURE );
    }

    for (spd = speedtab;;) {
        if (tmp == spd->bps)
            break;
        spd++;
        if (spd->bps == 0) {
            fprintf(stderr, "%s: speed %lu is unsupported\n",
                    pOptions->pzProgName, tmp);
            USAGE( EXIT_FAILURE );
        }
    }

    speed = spd->rate;
}

#endif /* defined(TEST_CLKTEST_OPTS) */

